    const [refreshToken, setRefreshToken] = useState("")
    const [expiresIn, setExpiresIn] = useState("")

    useEffect(() => {
        axios.post("http://localhost:3001/login", {
            code,
        }).then(res => {
            console.log(res.data)
        }).catch(err => {
            console.log(err)
            window.location = ''
        })
    }, [])


